INSERT INTO `portfolios` (`id`, `title`, `weblink`, `brief_description`, `text`, `image`) VALUES
	(1, 'WORLD OF WARCRAFT', 'bgwow.ru', 'Фан портал игры World of Warcraft', '        <div class="description">\r\n           <div class="padding_single">\r\n                <div class="title">Описание</div>\r\n                <div class="full_width">\r\n                    <div class="text">\r\n                       <p>\r\n                            BgWoW — информационный портал игры World of Warcraft. На сайте представлено обширное количество информации для игроков. На сайте есть все для World of Warcraft!\r\n                            <br><br>\r\n                            Нашей задачей было создать новостной сайт портал по игре World of Warcraft который бы мог заинтересовать пользователя как графикой так и информацией.\r\n                       </p>\r\n                    </div>\r\n                    <div class="text">\r\n                        <p>\r\n                            У нас получился интересный сайт, основанный на красивейших артах компании Blizzard, интересной верстке и наполненный интересными новостями. Мы также занялись продвижение сайта и поддержкой.\r\n                        </p>\r\n                    </div>\r\n                </div>\r\n           </div>\r\n        </div>\r\n        <div class="design_and_create">\r\n            <div class="padding_single">\r\n                <div class="title">ДИЗАЙН и создание</div>\r\n            </div>    \r\n                <img style="margin-top: 42px;" src="/img/portfolio/single/wow/01.png" alt="">\r\n                <img style="margin-top: 21px;" src="/img/portfolio/single/wow/02.png" alt="">\r\n                <img style="margin-top: 142px;" src="/img/portfolio/single/wow/03.png" alt="">\r\n        </div>\r\n        <div class="technical_information">\r\n           <div class="padding_single">\r\n                <div class="title">ТЕХНИЧЕСКАЯ ИНФОРМАЦИЯ</div>\r\n                <p>\r\n                    Макет был нарисован в Adobe Photoshop, после мы приступили к вёрстке страницы с помощи html, css и JavaScript с дальнейшей натяжкой на  CMS.\r\n                </p>\r\n           </div>\r\n        </div>\r\n\r\n', '/img/portfolio/single/wow/wow.jpg'),
	(2, 'The wizard', 'theunicorn.com.ua', 'Постер волшебника', '        <div class="description">\r\n           <div class="padding_single">\r\n                <div class="title">Описание</div>\r\n                <div class="full_width">\r\n                    <div class="text" style="width:100%">\r\n                       <p>\r\n                            Нашим дизайнером был создан постер А3 волшебник.\r\n                       </p>\r\n                    </div>\r\n                </div>\r\n           </div>\r\n        </div>\r\n        <div class="design_and_create">\r\n            <div class="padding_single">\r\n                <div class="title">ДИЗАЙН и создание</div>\r\n            </div>    \r\n                <img style="margin-top: 42px;" src="/img/portfolio/single/the_wizard/01.png" alt="">\r\n 	<img style="margin-top: 13px;" src="/img/portfolio/single/the_wizard/02.png" alt="">\r\n        </div>\r\n        <div class="technical_information">\r\n           <div class="padding_single">\r\n                <div class="title">ТЕХНИЧЕСКАЯ ИНФОРМАЦИЯ</div>\r\n                <p>\r\n                   При сооздании постера был использован Photoshop а также Illustrator.\r\n                </p>\r\n           </div>\r\n        </div>\r\n        \r\n', '/img/portfolio/single/the_wizard/the_wizard.jpg'),
	(3, 'Fable', 'fable-3.net', 'Фан сайт игры Fable', '        <div class="description">\r\n           <div class="padding_single">\r\n                <div class="title">Описание</div>\r\n                <div class="full_width">\r\n                    <div class="text">\r\n                       <p>\r\n                            Фан-сайт посвящен серии игр Fable. Здесь вы найдете прохождение Fable, секреты и коды, описания локаций, персонажей и квестов, полезные файлы и патчи для Fable.\r\n                       </p>\r\n                    </div>\r\n                    <div class="text">\r\n                        <p>\r\n                            Нашей задачей было создать фан сайты популярной игры Fable. Мы отнеслись к работе с большим энтузиазмом и желанием, т. к. мы любим играть в игры и Fable является одной из любимых игр. <br>\r\n                            У нас получился сказочный и яркий шаблон сайта. <br>\r\n                            Была проделана огромная работа по созданию сайта. <br>\r\n                            Мы нарисовал и сверстали более 20 страниц сайта. <br>\r\n                        </p>\r\n                    </div>\r\n                </div>\r\n           </div>\r\n        </div>\r\n        <div class="design_and_create">\r\n            <div class="padding_single">\r\n                <div class="title">ДИЗАЙН и создание</div>\r\n            </div>    \r\n                <img style="margin-top: 42px;" src="/img/portfolio/single/fable/01.png" alt="">\r\n                <img style="margin-top: 54px;" src="/img/portfolio/single/fable/02.png" alt="">\r\n                <img style="margin-top: 0;" src="/img/portfolio/single/fable/03.png" alt="">\r\n                <img style="margin-top: 45px;" src="/img/portfolio/single/fable/04.png" alt="">\r\n        </div>\r\n        <div class="technical_information">\r\n           <div class="padding_single">\r\n                <div class="title">ТЕХНИЧЕСКАЯ ИНФОРМАЦИЯ</div>\r\n                <p>\r\n                    Макет был нарисован в Adobe Photoshop, после мы приступили к вёрстке страницы с помощи html, css и JavaScript с дальнейшей натяжкой на  CMS.\r\n                </p>\r\n           </div>\r\n        </div>\r\n\r\n', '/img/portfolio/single/fable/fable.jpg'),
	(4, 'Stalker', 'stalker-2.com', 'Фан сайт игры Stalker', '        <div class="description">\r\n           <div class="padding_single">\r\n                <div class="title">Описание</div>\r\n                <div class="full_width">\r\n                    <div class="text">\r\n                       <p>\r\n                            Фан-сайт посвящен серии игр STALKER. Здесь вы найдете прохождение, секреты и коды, описания локаций, персонажей, полезные файлы моды и патчи.\r\n                       </p>\r\n                    </div>\r\n                    <div class="text">\r\n                        <p>\r\n                            Нашей задачей было создать фан-сайт популярной игры STALKER. У нас получился интересный и крутой шаблон сайта. <br>\r\n                            Была проделана огромная работа по созданию сайта. <br>\r\n                            Мы нарисовал и сверстали более 30 страниц сайта.\r\n                        </p>\r\n                    </div>\r\n                </div>\r\n           </div>\r\n        </div>\r\n        <div class="design_and_create">\r\n            <div class="padding_single">\r\n                <div class="title">ДИЗАЙН и создание</div>\r\n            </div>    \r\n                <img style="margin-top: 42px;" src="/img/portfolio/single/stalker/01.png" alt="">\r\n                <img style="margin-top: 17px;" src="/img/portfolio/single/stalker/02.png" alt="">\r\n                <img style="margin-top: 87px;" src="/img/portfolio/single/stalker/03.png" alt="">\r\n        </div>\r\n<!--         <div class="technical_information">\r\n           <div class="padding_single">\r\n                <div class="title">ТЕХНИЧЕСКАЯ ИНФОРМАЦИЯ</div>\r\n                <p>\r\n                    Макет был нарисован в Adobe Photoshop, после мы приступили к вёрстке страницы с помощи html, css и JavaScript с дальнейшей натяжкой на  CMS.\r\n                </p>\r\n           </div>\r\n        </div> -->\r\n\r\n', '/img/portfolio/single/sta